import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState('');

  useEffect(() => {
    fetchNotes();
  }, [notes]);

  const fetchNotes = async () => {
    try {
      const response = await axios.get('http://localhost:1995/notes');
      setNotes(response.data.results);
    } catch (error) {
      console.error('Error fetching notes:', error);
    }
  };

  const handleNoteChange = (event) => {
    setNewNote(event.target.value);
  };

  const handleAddNote = async () => {
    if (newNote.trim() === '') return;

    try {
      const response = await axios.post('http://localhost:1995/notes', {
        description: newNote
      });
      setNotes([...notes, response.data.results]);
      setNewNote('');
    } catch (error) {
      console.error('Error adding note:', error);
    }
  };

  const handleDeleteNote = async (id) => {
    try {
      await axios.delete(`http://localhost:1995/notes/${id}`);
      setNotes(notes.filter(note => note.note_id !== id));
    } catch (error) {
      console.error('Error deleting note:', error);
    }
  };

  return (
    <div className="App">
      <h1>Note Taking App</h1>

      <div>
        <input
          type="text"
          placeholder="Enter a new note"
          value={newNote}
          onChange={handleNoteChange}
        />
        <button onClick={handleAddNote}>Add Note</button>
      </div>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Note</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {notes.map(note => (
            <tr key={note.note_id}>
              <td>{note.note_id}</td>
              <td>{note.description}</td>
              <td>
                <button onClick={() => handleDeleteNote(note.note_id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
